mapper.select:
select id, good_type, good_name, good_num, good_detail, good_price, good_virtual,
good_down_virtual, status, create_time, modify_time, create_user,good_img,good_status,exchange_num
from t_good_info
WHERE 1=1

  AND good_name LIKE CONCAT('%',#{goodName},'%')


  AND good_type = #{goodType}


  AND good_status = #{goodStatus}


  AND status = #{status}

ORDER BY status DESC,id DESC

mapper.update:
update t_good_info
set good_status = #{goodStatus}
where id = #{id}

update t_good_info
set good_num = #{goodNum}
where id = #{id}
